mapper.select:
SELECT sellerName, managerTel, permitDate
FROM seller
WHERE sellerName=#{sellerName}

SELECT productMainImg, productName, customerPrice
FROM product
WHERE sellerName=#{sellerName}
LIMIT 4;

SELECT ProductMainImg, productNo, sellerName, productName, customerPrice
FROM product
WHERE sellerName = #{sellerName}

SELECT sellerName, sellerRes, sellerUrl, managerEmail
FROM seller
WHERE sellerName = #{sellerName}

mapper.update:
UPDATE seller SET
permit = 1, permitDate = sysdate()
WHERE sellerName = #{sellerName}
